mapper.select:
SELECT dept_id, dept_name
  FROM tbl_department_mst
 WHERE del_flag = 0;

mapper.insert:
INSERT INTO tbl_department_mst(
  dept_name, 
  reg_id, 
  update_id
)
VALUES (
  #{dept_name}, 
  #{reg_id}, 
  #{update_id}
);

mapper.update:
UPDATE tbl_department_mst
   SET dept_name=#{department.dept_name}
 WHERE dept_id = #{id}

UPDATE tbl_department_mst
   SET del_flag=1
 WHERE dept_id = #{id}

UPDATE tbl_department_mst
   SET del_flag=0
 WHERE dept_id = #{id}
