mapper.select:
SELECT dev_type_id, dev_type_name, dev_type_sh_name
  FROM tbl_dev_type_mst
 WHERE del_flag = 0;

mapper.insert:
INSERT INTO tbl_dev_type_mst(
    dev_type_name, 
    dev_type_sh_name, 
    reg_id
)
VALUES (
    #{dev_type_name}, 
    #{dev_type_sh_name}, 
    1
);

mapper.update:
UPDATE tbl_dev_type_mst
   SET del_flag = 1
WHERE dev_type_id = #{id}

UPDATE tbl_dev_type_mst
   SET del_flag = 0
WHERE dev_type_id = #{id}
