mapper.select:
SELECT user_role_id, emp_id, role_id, del_flag
 FROM tbl_multi_role
WHERE user_role_id = #{id}

mapper.insert:
INSERT INTO tbl_multi_role(
    emp_id, 
    role_id, 
    reg_id
    )
VALUES (
    #{emp_id}, 
    #{role_id},
    1
    );

mapper.update:
UPDATE tbl_multi_role
   SET del_flag = 1
WHERE emp_id = #{id}

UPDATE tbl_multi_role
   SET del_flag = 0
WHERE emp_id = #{id}
