mapper.update:
UPDATE tbl_personal_info
   SET fname=#{personalInfo.fname}, 
       lname=#{personalInfo.lname}, 
       mname=#{personalInfo.mname}, 
       email=#{personalInfo.email}

WHERE pid = #{id}

UPDATE tbl_personal_info
   SET del_flag = 1
 WHERE pid = #{id}

UPDATE tbl_personal_info
   SET del_flag = 0
 WHERE pid = #{id}
