mapper.update:
UPDATE tbl_proj_tech
       SET tech_id=#{projectTechnology.tech_id}, proj_id=#{projectTechnology.proj_id},
        reg_id=#{projectTechnology.reg_id}, update_id=#{projectTechnology.update_id}
    WHERE proj_tech_id = #{id}

UPDATE tbl_proj_tech
   SET del_flag=1
WHERE proj_tech_id = #{id}
