mapper.select:
SELECT proj_tech_id, tech_id, proj_id, del_flag
  FROM tbl_proj_tech
 WHERE proj_tech_id = #{id};

mapper.insert:
INSERT INTO tbl_proj_tech (
    tech_id, 
    proj_id
)
VALUES (
    #{tech_id}, 
    #{proj_id}
)

mapper.update:
UPDATE tbl_proj_tech
   SET del_flag = 1
WHERE proj_tech_id = #{id}

UPDATE tbl_proj_tech
   SET del_flag = 0
WHERE proj_tech_id = #{id}
