mapper.update:
UPDATE tbl_role_mst
   SET title=#{role.title}, 
       role_sh_name=#{role.role_sh_name}, 
       role_user_level=#{role.role_user_level}
 WHERE role_id = #{id}

UPDATE tbl_role_mst
   SET del_flag = 1
 WHERE role_id = #{id}

UPDATE tbl_role_mst
   SET del_flag = 0
 WHERE role_id = #{id}
