mapper.select:
SELECT section_id, section_name, section_sh_name, section_desc, dept_id, del_flag
  FROM tbl_section_mst
 WHERE section_id = #{id};

mapper.update:
UPDATE tbl_section_mst
   SET del_flag = 1
WHERE section_id = #{id}

UPDATE tbl_section_mst
   SET del_flag = 0
WHERE section_id = #{id}
