mapper.select:
SELECT status_code, status_name
 FROM tbl_status_mst
WHERE del_flag = 0;

mapper.insert:
INSERT INTO tbl_status_mst (
    status_code, 
    status_name, 
    reg_id
)
VALUES (
    #{status_code}, 
    #{status_name}, 
    1
);

mapper.update:
UPDATE tbl_status_mst
   SET del_flag = 1
WHERE status_code = #{code}

UPDATE tbl_status_mst
   SET del_flag = 0
WHERE status_code = #{code}
