mapper.select:
SELECT upid, emp_id, proj_id, del_flag
  FROM tbl_user_project
 WHERE upid = #{id};

mapper.insert:
INSERT INTO tbl_user_project (
    emp_id, 
    proj_id, 
    reg_id
)
VALUES (
    #{emp_id}, 
    #{proj_id}, 
    1
);

mapper.update:
UPDATE tbl_user_project
   SET del_flag = 1
WHERE proj_id = #{id}

UPDATE tbl_user_project
   SET del_flag = 0
WHERE proj_id = #{id}
